Core Docker commands   docker ps -a
  docker run --rm --name {containerName} {imageName}
  docker run --rm -it --name {containerName} {imageName}
  docker logs -f {containerName}: Continuos logs
  docker exec -it {containerName} {cmd-to-execute}
  alias ccx='f(){ [ -n &quot;$1&quot; ] &amp;&amp; docker exec -it &quot;$1&quot; /bin/bash; unset -f f; }; : Creates bash session in running container
  docker inspect {containerName}
  Yellow=&rsquo;\033[0;33m
  IRed=&rsquo;\033[0;91m&rsquo;
  IBlue=&rsquo;\033[0;94m&rsquo;
  NC=&rsquo;\033[0m&rsquo;
  echo -e &quot;$(docker ps --format &quot;${Yellow}{{.Names}}${NC}\n\tStatus:${IRed} {{.Status}}${NC}\n\t${IBlue}Container ID:${NC} {{.ID}}\t${IBlue}Command:${NC} {{.Command}}\n\t${IBlue}Image:${NC} {{.Image}}&quot; )&quot;
  VVBODY=&quot;$(docker ps -a --format &quot;${IRed}{{.Status}}${NC},${Yellow}{{.Names}}${NC} , {{.ID}} , {{.CreatedAt}}&quot; )&quot;; echo -e &quot;$VVBODY&quot; | column -t -s,
  echo -e &quot;$(docker ps -a --format &quot;${Yellow}{{.Names}}${NC}\n\tStatus:${IRed} {{.Status}}${NC}\n\t${IBlue}Container ID:${NC} {{.ID}}\t${IBlue}Command:${NC} {{.Command}}\n\t${IBlue}Image:${NC} {{.Image}}\n\t${IBlue}CreatedAt:${NC} {{.CreatedAt}}&quot; )&quot;
  VVHEAD=&quot;${Black}${On_White}\tSTATUS,CONTAINER-ID,CMD,IMAGE,CREATED AT${NC}&quot;;VVBODY=&quot;$(docker ps -a --format &quot;${Yellow}{{.Names}}${NC}\n\t${IRed}{{.Status}}${NC} , {{.ID}} , {{.Command}} , {{.Image}} , {{.CreatedAt}}&quot; )&quot;;echo -e &quot;$VVHEAD\n$VVBODY&quot; | column -t -s,
  docker build -t {imageTag} .
  docker build -t {imageTag} -f {DockerfileName} . : Context set to current folder
  docker run -it --rm --mount type=bind,src=/c/Users/USR/Documents/folder,dst=/app/publish --name {appName} {imagetag}
  docker rm $(docker ps -a -q)
  [ -n &quot;$(docker ps -a -q)&quot; ] &amp;&amp; echo &quot;Removing idle containers&quot; &amp;&amp; docker rm $(docker ps -q -f &quot;status=exited&quot;)
  docker rmi $(docker images | awk '{print $3}')
  docker rmi $(docker images | grep &quot;&lt;none&gt;&quot; | awk '{print $3}')
  docker images prune
  docker images ls
  docker volumes prune
  docker volumes ls
  docker network ls
  docker network rm {name}
  Core docker-compose commands  docker-compose up --build docker-compose --verbose up --build -d docker-compose down docker-compose -f file01.yml -f file02.yml up docker-compose run {serviceName} docker-compose stop &amp;&amp; docker-compose rm -f &quot;&quot;: Removes only the containers. It doe snot remove volumes or network adapters f(){ docker-compose stop &quot;$@&quot; &amp;&amp; docker-compose rm -f &quot;$@&quot;; unset -f f; }; f : To be used in a bash function  Other commands  export DISPLAY=$(cat /etc/resolv.conf | grep nameserver | awk '{print $2}'):0 export DOCKER_HOST='tcp://localhost:2375' export DOCKER_HOST=unix:///var/run/docker.sock

Core Git commands  git status git checkout git rev-parse --abbrev-ref HEAD: Current branch name git branch -a: List all branches git checkout -b git log --pretty=oneline git diff {branch1}..{branch2} git diff {fileName} git add {fileName01} {fileName02} ... {fileName0N} git commit -m &quot;Commit msg here&quot; git push  or  git push --set-upstream {upstream-ref} {which-branch} git stash git stash pop git commit --amend -m {new-commit-message} git commit -amend ## Opens vi to edit commit msg. Type a for append. When done, press ESC then press : and type wq meaning write and exit git cherry-pick {commitID01} {commitID02} ... {commitID0N} git rebase -i {commit-id-to-start-from}: squash, fix commits, order commits, drop commits git fetch master: Bring changes but do not merge them git reset HEAD: Reset unstaging changes (Reset &ldquo;git add {&hellip;}&quot;) git reset HEAD^: Branch reset - reset last commit, move head to previous commit git config --global alias.uncommit 'reset HEAD^': Use this as git uncommit git reset HEAD {file}: Unstage the file but maintain the modifications git checkout -- {file}: Revert a file back to the state it was in before any changes Rebasing commits from master into current branch  # master(public): c1 - c37 # feaure(mine): c1 - c2 - c3 - [...] - c36 git checkout {master-br-name} git rebase {master-br-name} {feature-br-name} # Final: c1 - c37 - c2 - c3 - [...] - c36 # Branch changed here would be the feature branch (aka current branch at the end is &quot;feature&quot; branch) # If conflict arises, rebase will stop and indicate where the conflict is (what file) # - Resolve conflict using emacs: # - Remove conflict indicators # - Choose/amend changes to desired behavior # - Save file and exit # - `git rebase --continue`: It will continue rebase operation   git remote -v
  git remote rm {nameRemote}
  git remote add {nameRemote} {ssh:git_url}
  git rev-parse --abbrev-ref HEAD: Current branch
  git branch -D {branchName}: Removes branch even if it has not been pushed
  git branch -m {oldName} {newName}: Rename branch
  git mv {oldFileName} {newFileName}
  GIT_TRACE_PERFORMANCE=1 git status: To get a report in git ops and their performance
  git submodule add {URL-Repo}.git {folder}
  git submodule update --remote --merge
  git submodule update --init --recursive

